// Extract the <AdaptationSet> with contentType="audio"
const audioAdaptationSetMatch = manifest.match(/<AdaptationSet[^>]*contentType="audio"[^>]*>[\s\S]*?<\/AdaptationSet>/);

if (!audioAdaptationSetMatch) {
  return [{ json: { error: "Audio AdaptationSet not found" } }];
}

// Extract <BaseURL> within the audio section
const baseUrlMatch = audioAdaptationSetMatch[0].match(/<BaseURL>(.*?)<\/BaseURL>/);

if (!baseUrlMatch || !baseUrlMatch[1]) {
  return [{ json: { error: "BaseURL not found in audio AdaptationSet" } }];
}

// Decode HTML entities like &amp;
const decodedUrl = baseUrlMatch[1].replace(/&amp;/g, '&');

return [
  {
    json: {
      audio_url: decodedUrl
    }
  }
