const enhancedContent = $('AI Content Optimizer').item.json.message.content;
const aiHashtags = $('AI Hashtag Generator').item.json.message.content;

// Parse AI hashtags (should be JSON array)
let optimizedHashtags = [];
try {
  optimizedHashtags = JSON.parse(aiHashtags);
} catch (e) {
  // Fallback: extract hashtags from text
  optimizedHashtags = aiHashtags.match(/#\w+/g) || [];
}

// Combine with existing hashtags if any
const existingHashtags = originalData.hashtags ? JSON.parse(originalData.hashtags) : [];
const finalHashtags = [...new Set([...existingHashtags, ...optimizedHashtags])];

// Platform-specific hashtag limits
let hashtagLimit = 30; // Default
switch (originalData.platform.toLowerCase()) {
  case 'instagram':
    hashtagLimit = 30;
    break;
  case 'tiktok':
    hashtagLimit = 100; // TikTok allows more
    break;
  case 'facebook':
    hashtagLimit = 5; // Facebook prefers fewer
    break;
  case 'pinterest':
    hashtagLimit = 20;
    break;
}

const limitedHashtags = finalHashtags.slice(0, hashtagLimit);

return {
  json: {
    ...originalData,
    content: enhancedContent,
    hashtags: JSON.stringify(limitedHashtags),
    ai_enhanced: true,
    original_content: originalData.content,
    ai_hashtag_count: optimizedHashtags.length
  }
};

